<?php  require_once 'conn.php';require_once './statusCode.php';$statusCode = $_POST['statusCode'];if (isset($statusCode) && isValidStatusCode($statusCode)){$endpoint = "SELECT * FROM config_data WHERE type = 'cclass'"; $result = $conn->query($endpoint);if ($conn->error) { die("FALSE：" . $conn->error); }$cclass = $result -> fetch_all(MYSQLI_ASSOC);$endpoin = "SELECT * FROM config_data WHERE type = 'part'"; $resul = $conn->query($endpoin);if ($conn->error) { die("FALSE：" . $conn->error); }$part = $resul -> fetch_all(MYSQLI_ASSOC);$endpoi = "SELECT * FROM config_data WHERE type = 'year'"; $resu = $conn->query($endpoi);if ($conn->error) { die("FALSE：" . $conn->error); }$year = $resu -> fetch_all(MYSQLI_ASSOC);header("Content-Type: application/json; charset=utf-8"); echo json_encode(["errno" => 0, "cclass" => $cclass,"part" => $part,"year" => $year,]);$conn->close();  }else{header('Content-Type: application/json'); echo json_encode([ 'errno' => 1, 'message' => '无权访问！！！' ]);}?>  