<?php require_once 'conn.php'; require_once './statusCode.php'; $statusCode = $_POST['statusCode']; $nowNumber = $_POST['nowNumber']; if (isset($statusCode) && isValidStatusCode($statusCode)){ $info = $conn->prepare("SELECT * FROM userdata WHERE id = ?"); $info->bind_param('s', $nowNumber); if($info->execute()){ $resul = $info->get_result(); $infoData = $resul->fetch_object();   header('Content-Type: application/json'); echo json_encode([ 'errno' => 0, 'info' => $infoData ]); }else{header('Content-Type: application/json'); echo json_encode([ 'errno' => 1, 'message' => '程序出错啦！！！' ]);} }else{header('Content-Type: application/json'); echo json_encode([ 'errno' => 1, 'message' => '无权访问！！！' ]);} ?>  