<?php  require_once 'conn.php'; require_once './statusCode.php'; $statusCode = $_POST['statusCode']; $userPhone = $_POST['userPhone']; if (isset($statusCode) && isValidStatusCode($statusCode)){ $info = $conn->prepare("SELECT id,partName,partIdentity,remarks,timeData FROM remark_data WHERE userPhone = ?"); $info->bind_param('s', $userPhone); if($info->execute()){ $resul = $info->get_result(); $infoData = $resul-> fetch_all(MYSQLI_ASSOC); header('Content-Type: application/json'); echo json_encode([ 'errno' => 0, 'info' => $infoData ]); }else{header('Content-Type: application/json'); echo json_encode([ 'errno' => 1, 'message' => '程序出错啦！！！' ]);} }else{header('Content-Type: application/json'); echo json_encode([ 'errno' => 1, 'message' => '无权访问！！！' ]);} ?>  