<?php require_once 'conn.php'; require_once './statusCode.php'; $name = $_POST['name']; $phone = $_POST['phone']; $msStatus = $_POST['msStatus']; $statusCode = $_POST['statusCode']; if (isset($statusCode) && isValidStatusCode($statusCode)) { $int = $conn->prepare("UPDATE userdata SET status = ? WHERE name = ? AND phone = ?"); $int->bind_param('sss',$msStatus,$name,$phone); if($int->execute() === TRUE){ header('Content-Type: application/json'); echo json_encode([ 'errno' => 0, 'message' => '面试状态更改成功！！']); }else{header('Content-Type: application/json'); echo json_encode([ 'errno' => 1, 'message' => '系统错误，错误码：3306...请重试或者联系管理员！作者QQ：'.$systemAdminQq ]);} }else{header('Content-Type: application/json'); echo json_encode([ 'errno' => 1, 'message' => '无权访问！！！' ]);} ?>  